import React from "react";
import CardChoices from "./components/CardChoices";
import Wrapper from "./components/Wrapper";
import friends from "./choices.json";
import Header from "./components/Header";
import Jumbotron from "./components/Jumbotron";
import "./App.css";

class App extends React.Component {
  state = {
    friends,
    clickedFriends: [],
    score: 0
  };

  imageClick = event => {
    const currentFriend  = event.target.alt;
    const FriendClicked = this.state.clickedFriends.indexOf(currentFriend) > -1;

    if (FriendClicked) {
      this.setState({
        friends: this.state.friends.sort(function(a, b){
          return 0.5 - Math.random();
        }),
        clickedFriends: [],
        score: 0
      });
      alert("You lost. Play Again?");

    } else {
      this.setState({
        friends: this.state.friends.sort(function(a,b){
          return 0.5 - Math.random();
        }),
        clickedFriends: this.state.clickedFriends.concat(
          currentFriend
        ),
        score: this.state.score + 1
      },
      () => {
        if (this.state.score === 12) {
          alert("Congrats You Win!");
          this.setState({
            friends: this.state.friends.sort(function(a, b){
              return 0.5 - Math.random();
            }),
            clickedFriends: [],
            score: 0
          });
        }
      }
      );
    }
  };

  handleRemove = id => {
    const filteredFriends = this.state.friends.filter(f => f.id !== id);
    this.setState({ friends: filteredFriends });
  };

  render() {
    return (
      <Wrapper>
        <Header></Header>
        <Jumbotron></Jumbotron>
        <div className="container cards">
          <div className="row">
            {this.state.friends.map(f => (
              <CardChoices
                id={f.id}
                image={f.image}
                handleClick={() => this.handleRemove(f.id)}
              />
            ))}
          </div>
        </div>
      </Wrapper>
    );
  }
}

export default App;
